package com.seereal.algi.dto.regularDonation;import com.seereal.algi.model.regularDonation.DonationCostResult;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;public class DonationCostResultDto {    @Getter    @NoArgsConstructor    public static class Request {        private String name;        private Integer targetNum;        private Integer cost;        private String etc;        public DonationCostResult convertToEntity() {            return DonationCostResult.builder()                    .name(this.name)                    .targetNum(this.targetNum)                    .cost(this.cost)                    .etc(this.etc)                    .build();        }    }    @Getter    @NoArgsConstructor    public static class Response {        private String name;        private Integer targetNum;        private Integer cost;        private String etc;        private String receiptUrl;        public Response(DonationCostResult result) {            this.name = result.getName();            this.targetNum = result.getTargetNum();            this.cost = result.getCost();            this.etc = result.getEtc();            this.receiptUrl = result.getReceiptUrl();        }    }}