package com.seereal.algi.dto.regularDonation;import com.seereal.algi.model.regularDonation.DonationCostResult;import com.seereal.algi.model.regularDonation.DonationResult;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import java.util.List;public class DonationResultDto {    @NoArgsConstructor    @Getter    public static class Request {        private String content;        private Integer quarter;        public DonationResult convertToEntity() {            return DonationResult.builder()                    .content(this.content)                    .quarter(this.quarter)                    .build();        }    }    @NoArgsConstructor    @AllArgsConstructor    @Getter    @Builder    public static class Response{        private String content;        private Integer quarter;        private List<DonationCostResultDto.Response> costResult;    }}