package com.seereal.algi.model.portfolio;import com.seereal.algi.model.category.Category;import com.seereal.algi.model.user.User;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import javax.persistence.*;import java.util.ArrayList;import java.util.List;@Getter@NoArgsConstructor@Entitypublic class Portfolio {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "portfolio_id")    private Long id;    private String name;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;    private String categories;    @Builder    public Portfolio(String name, User user, String categories) {        this.name = name;        this.user = user;        this.categories = categories;    }}